package com.openxcell.data.datasource


import androidx.lifecycle.LiveData
import com.openxcell.data.api.ApiInterface
import com.openxcell.data.db.UserDao
import com.openxcell.data.pojo.DataEntity
import com.openxcell.data.pojo.ResponseData
import com.openxcell.data.pojo.UserModel
import com.openxcell.data.pojo.UserRepo
import com.openxcell.data.repository.AuthRepository
import com.openxcell.utills.SubscribeWithOutType
import com.openxcell.utills.makeThreadSafe
import io.reactivex.Single
import io.reactivex.SingleOnSubscribe
import java.util.*
import javax.inject.Inject

class AuthDataSource @Inject constructor(
    private val apiInterface: ApiInterface,
    private val userDao: UserDao
) : AuthRepository {


    override fun getUserListByPage(page: String): Single<UserRepo> =
        apiInterface.getUserListByPage(page)
            .makeThreadSafe()
            .doOnSuccess {
                if (it.data != null)
                    for (dataEntity: DataEntity in it.data!!) {
                        dataEntity.postImage= getRandomeImage()
                    }

            }

    private fun getRandomeImage(): String {
        val images :List<String> = listOf("https://www.forest-trends.org/wp-content/uploads/2017/04/acadia_np_622419-High-Res.jpg",
                "data:image/jpeg;base64,",
                "https://pureadvantage.org/wp-content/uploads/2017/04/Harry_and_Rowena_Kennedyweb.jpg",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQN21YAX5YZmfPb2hKpGiz0eaDajvBGf7pqze1yD6DOYHZ4n6_ljQ",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTn3JEmAxLuCGeVt9u9ZdUdmezdcSmHAszZAaq-czeGPfObji0c")
        return images.shuffled().take(1)[0]
    }


    override fun getAll(): LiveData<List<UserModel>> = userDao.getAllList()

    override fun userLogin(email: String, password: String): Single<ResponseData<UserModel>> {
        val valuesMap = HashMap<String, String>()
        valuesMap["deviceToken"] =
            "eUHuPNmygJ8:APA91bGmFECbbWx62-xCAllYI2fBi-AafindpATHAoQhpITMMSznyauwCpYqjuXBy0yRCqdj5N34pnTGeGv1C8tzTvq7sTU_4AxqPSdhcXtmKO-D6vwWLndV_7VRCL0ZxJ0qsN-EECX7"
        valuesMap["deviceType"] = "android"
        valuesMap["userName"] = email
        valuesMap["socialId"] = ""
        valuesMap["password"] = password
        valuesMap["loginType"] = "normal"
        return apiInterface.userLogin(valuesMap).makeThreadSafe()
            .doOnSuccess {
                insertUser(it.data)
            }
    }

    private fun insertUser(user: UserModel?) {
        Single.create(SingleOnSubscribe<Boolean> {
            user?.let {
                userDao.insertUser(user)
            }
        }).makeThreadSafe()
            .subscribe(SubscribeWithOutType())
    }


}